mapper.select:
select
  id, nickname, date, src, description, detailInfo, clincID, userID
from MedicalExaminationFile


        and id = #{id}


        and nickname = #{nickname}


        and date = #{date}


        and src = #{src}


        and description = #{description}


        and detailInfo = #{detailinfo}


        and clincID = #{clincid}


        and userID = #{userid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from MedicalExaminationFile


        and id = #{id}


        and nickname = #{nickname}


        and date = #{date}


        and src = #{src}


        and description = #{description}


        and detailInfo = #{detailinfo}


        and clincID = #{clincid}


        and userID = #{userid}
