mapper.select:
select
  drugId, drugName, jj, xz, spmc, jx, syz, yfyl, zycf, zysx, etyy, gg, yfjbrqfnyy, manu, zc, hypy, tymc, pzwh, zxbz, ywxhzy, yxq, blfy, lryy, price
from drugInfo


        and drugId = #{drugid}


        and drugName = #{drugname}


        and jj = #{jj}


        and xz = #{xz}


        and spmc = #{spmc}


        and jx = #{jx}


        and syz = #{syz}


        and yfyl = #{yfyl}


        and zycf = #{zycf}


        and zysx = #{zysx}


        and etyy = #{etyy}


        and gg = #{gg}


        and yfjbrqfnyy = #{yfjbrqfnyy}


        and manu = #{manu}


        and zc = #{zc}


        and hypy = #{hypy}


        and tymc = #{tymc}


        and pzwh = #{pzwh}


        and zxbz = #{zxbz}


        and ywxhzy = #{ywxhzy}


        and yxq = #{yxq}


        and blfy = #{blfy}


        and lryy = #{lryy}


        and price = #{price}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from drugInfo


        and drugId = #{drugid}


        and drugName = #{drugname}


        and jj = #{jj}


        and xz = #{xz}


        and spmc = #{spmc}


        and jx = #{jx}


