mapper.select:
select
  id, total, createTime, status, closeTime, doctorId
from order


        and id = #{id}


        and total = #{total}


        and createTime = #{createtime}


        and status = #{status}


        and closeTime = #{closetime}


        and doctorId = #{doctorid}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from order


        and id = #{id}


        and total = #{total}


        and createTime = #{createtime}


        and status = #{status}


        and closeTime = #{closetime}


        and doctorId = #{doctorid}
